INSERT INTO ricette (numero_porzioni, tempo_preparazione, foto, ingredienti, testo, title) VALUES(2, 120, 'https://images.unsplash.com/photo-1574894709920-11b28e7367e3?q=80&w=1935&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', '200g acqua,100 gr sugo', 'Prima di tutto preparate preparate il ragù, lavando e tritando cipolla, il sedano e carota. Poi aggiungeteli in una pentola alta con l’olio e fate soffriggere per circa 5 minuti e aggiungete anche il rosmarino finemente tritato.','Lasagna');
